        example_cluster = aws.emr.Cluster("exampleCluster",
            core_instance_group={},
            ec2_attributes={
                "subnet_id": example_subnet.id,
            },
            master_instance_group={
                "instance_count": 3,
            },
            release_label="emr-5.24.1",
            termination_protection=True)
